import React, { useState } from 'react';
import Profiles from './profiles';
import { Leaderboard } from './database';
import '../board.css'; // Import the CSS file

export default function Board() {
  const [period, setPeriod] = useState(0);
  const [difficulty, setDifficulty] = useState('All');
  const [domain, setDomain] = useState('All');

  const handlePeriodChange = (newPeriod) => {
    setPeriod(newPeriod);
  };

  const handleDifficultyChange = (newDifficulty) => {
    setDifficulty(newDifficulty);
  };

  const handleDomainChange = (newDomain) => {
    setDomain(newDomain);
  };

  return (
    <div className="container">
      <div className="sidebar">
        <h2>Select Time Period:</h2>
        <div className="sidebar">
      <h2>Select Time Period:</h2>
      <button onClick={() => handlePeriodChange(0)}>All-Time</button>
      <button onClick={() => handlePeriodChange(7)}>7 Days</button>
      <button onClick={() => handlePeriodChange(30)}>30 Days</button>
    
      <h2>Select Difficulty:</h2>
        <div className="difficulty-buttons">
          <button onClick={() => handleDifficultyChange('All')}>All</button>
          <button onClick={() => handleDifficultyChange('Easy')}>Easy</button>
          <button onClick={() => handleDifficultyChange('Medium')}>Medium</button>
          <button onClick={() => handleDifficultyChange('Difficult')}>Difficult</button>
        </div>

        {/* Domain selection */}
        <h2>Select Domain:</h2>
        <div className="domain-buttons">
          <button onClick={() => handleDomainChange('All')}>All</button>
          <button onClick={() => handleDomainChange('Python')}>Python</button>
          <button onClick={() => handleDomainChange('Linux')}>Linux</button>
          {/* Add other domains as needed */}
        </div>
      </div>
      </div>
      <div className="board" style={{ marginLeft: '200px' }}>
      <h1 className='leaderboard'>{getLeaderboardHeading(period)}</h1>
      <Profiles Leaderboard={between(Leaderboard, period)} />
    </div>
    </div>
  );
}

function between(data, period, selectedDomain, selectedDifficulty) {
    const today = new Date();
    const previous = new Date(today);
    previous.setDate(previous.getDate() - period);
  
    let filter = data.filter(val => {
      const userDate = new Date(val.dt);
      const withinPeriod = period === 0 || (previous <= userDate && today >= userDate);
      const domainMatch = selectedDomain === 'All' || val.domain === selectedDomain;
      const difficultyMatch = selectedDifficulty === 'All' || val.difficulty === selectedDifficulty;
      return withinPeriod && domainMatch && difficultyMatch;
    });
  
    return filter.sort((a, b) => b.score - a.score);
  }
  

function getLeaderboardHeading(period) {
  return period === 0 ? 'Leaderboard ' : 'Leaderboard';
}
